CREATE OR REPLACE FUNCTION obter_se_campo_integ_sup ( nr_sequencia_p bigint, vl_dominio_p text) RETURNS varchar AS $body$
DECLARE


ie_evento_w			varchar(15);
ie_forma_w			varchar(15);
ie_mostra_w			varchar(1) := 'N';


BEGIN

select	ie_evento,
	ie_forma
into STRICT	ie_evento_w,
	ie_forma_w
from	sup_parametro_integracao
where	nr_sequencia = nr_sequencia_p;

if (ie_evento_w = 'ME') and (ie_forma_w = 'E') and (vl_dominio_p in ('OE')) then
	ie_mostra_w := 'S';
elsif (ie_evento_w = 'CM') and (ie_forma_w = 'E') and (vl_dominio_p in ('GM')) then
	ie_mostra_w := 'S';
end if;


return	ie_mostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
