CREATE OR REPLACE FUNCTION obter_se_cancela_cotacao (nr_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
/*Esta function é utilizada para saber se pode cancelar uma cotaçaõ de compras*/
 
	 
ie_cancela_w				varchar(1);
nr_ordem_compra_w			bigint;
nr_seq_motivo_cancel_w			bigint;	
qt_existe_itens_entregue_w			bigint;
qt_existe_itens_nao_cancel_w		bigint;

c01 CURSOR FOR	 
SELECT	a.nr_ordem_compra 
from	cot_compra_resumo_v a 
where	a.nr_cot_compra   = nr_cot_compra_p 
and	(a.nr_ordem_compra IS NOT NULL AND a.nr_ordem_compra::text <> '');
	

BEGIN 
ie_cancela_w := 'S';
 
open C01;
loop 
fetch C01 into	 
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	nr_seq_motivo_cancel 
	into STRICT	nr_seq_motivo_cancel_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_compra_w;
	 
	if (coalesce(nr_seq_motivo_cancel_w::text, '') = '') and (ie_cancela_w = 'S') then 
		 
		select	count(*) 
		into STRICT	qt_existe_itens_entregue_w 
		from	ordem_compra_item_entrega 
		where	(qt_real_entrega IS NOT NULL AND qt_real_entrega::text <> '') 
		and	nr_ordem_compra = nr_ordem_compra_w;
 
		if (qt_existe_itens_entregue_w > 0) and (ie_cancela_w = 'S') then 
			ie_cancela_w := 'N';
		end if;
 
		if (ie_cancela_w = 'S') then 
			select	count(*) 
			into STRICT	qt_existe_itens_nao_cancel_w 
			from	ordem_compra_item_entrega 
			where	coalesce(dt_cancelamento::text, '') = '' 
			and	nr_ordem_compra = nr_ordem_compra_w;
			 
			if (qt_existe_itens_nao_cancel_w > 0) then 
				ie_cancela_w := 'N';
			end if;
		end if;
	end if;
	end;
end loop;
close C01;
 
return	ie_cancela_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_cancela_cotacao (nr_cot_compra_p bigint) FROM PUBLIC;

