CREATE OR REPLACE FUNCTION obter_se_carat_inter_regra ( ie_Tipo_atendimento_p bigint, cd_carater_internacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
qt_regra_w		integer;
cd_estabelecimento_w	integer;


BEGIN


cd_estabelecimento_w	:=	wheb_usuario_pck.get_cd_estabelecimento;

select	count(*)
into STRICT	qt_regra_w
from	regra_carater_tipo_atend
where	ie_tipo_atendimento	= ie_tipo_atendimento_p
and	cd_carater_internacao	= cd_carater_internacao_p
and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w
and	ie_situacao		= 'A';

if (qt_regra_w > 0) then
	begin
	ds_retorno_w	:= 'S';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
