CREATE OR REPLACE FUNCTION obter_se_cartao_fid_vencido ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_reg_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	pf_cartao_fidelidade
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;


if (qt_reg_w	> 0) then
	select	count(*)
	into STRICT	qt_reg_w
	from	pf_cartao_fidelidade
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	dt_inicio_validade	< clock_timestamp()
	and	coalesce(dt_fim_validade,clock_timestamp() + interval '1 days') > clock_timestamp();

	if (qt_reg_w	= 0) then
		return 'S';
	end if;
end if;

return	'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
