SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_carta_mensagem (nr_seq_carta_p bigint, nr_seq_carta_mae_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mensagem_w		varchar(1);
nm_usuario_w	varchar(15) := wheb_usuario_pck.get_nm_usuario;


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_mensagem_w
from	carta_comunicacao
where  	nr_seq_carta in (	SELECT nr_sequencia
							FROM	carta_medica
							WHERE nr_seq_carta_mae = nr_seq_carta_mae_p
							
union all

							SELECT 	coalesce(nr_seq_carta_mae,nr_sequencia)
							FROM	carta_medica
							WHERE nr_sequencia = nr_seq_carta_p)
and		coalesce(nm_destinatario,nm_usuario_w) = nm_usuario_w
and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and		coalesce(dt_inativacao::text, '') = ''
and		ie_leitura	= 'N'
order by dt_atualizacao_nrec desc;

return	ie_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
