CREATE OR REPLACE FUNCTION obter_se_case_atend_planejado ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w					varchar(1) := 'N';
ie_case_w						varchar(1) := 'N';


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	Select  coalesce(max('S'),'N')
	into STRICT	ie_case_w
	from	episodio_paciente a,
			atendimento_paciente b
	where	b.nr_seq_episodio = a.nr_sequencia
	and		b.nr_atendimento = nr_atendimento_p;
	
	if ( ie_case_w = 'S') then	
		
		Select 	obter_se_atendimento_futuro(nr_atendimento_p)				
		into STRICT	ie_retorno_w
		;
	
	end if;
	
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
