CREATE OR REPLACE FUNCTION obter_se_case_valido_transf ( nr_seq_episodio_p episodio_paciente.nr_sequencia%TYPE ) RETURNS varchar AS $body$
DECLARE


    ie_case_valido_transferencia_w     varchar(1);
    nr_atendimento_w     atendimento_paciente.nr_atendimento%TYPE;
    dt_referencia_w      episodio_paciente.dt_episodio%TYPE;
    dt_validade_case_w   atendimento_paciente_inf.dt_validade%TYPE;

BEGIN
    ie_case_valido_transferencia_w := 'S';

    IF (nr_seq_episodio_p IS NOT NULL AND nr_seq_episodio_p::text <> '') THEN
        SELECT
            MAX(ep.dt_episodio),
            MAX(ap.nr_atendimento)
        INTO STRICT
            dt_referencia_w,
            nr_atendimento_w
        FROM
            episodio_paciente      ep
            JOIN atendimento_paciente   ap ON ( ap.nr_seq_episodio = ep.nr_sequencia )
        WHERE
            ep.nr_sequencia = nr_seq_episodio_p;

        dt_validade_case_w := obter_validade_case(dt_referencia_w, nr_atendimento_w);

        IF (TRUNC(dt_validade_case_w) >= TRUNC(clock_timestamp())) THEN
            ie_case_valido_transferencia_w := 'N';
        ELSE
            ie_case_valido_transferencia_w := 'S';
        END IF;
    END IF;

    return ie_case_valido_transferencia_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
