CREATE OR REPLACE FUNCTION obter_se_categoria_lib_func (cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w		varchar(01)	:= 'S';
qt_regra_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	categoria_conv_estab_func
where	cd_convenio		= cd_convenio_p
and	cd_estabelecimento	= cd_estabelecimento_p
and	cd_funcao		= cd_funcao_p;

if (qt_regra_w	<> 0) then
	select	coalesce(max('S'), 'N')
	into STRICT	ie_liberado_w
	from	categoria_conv_estab_func
	where	cd_convenio		= cd_convenio_p
	and	cd_categoria		= cd_categoria_p
	and	cd_funcao		= cd_funcao_p
	and	cd_estabelecimento	= cd_estabelecimento_p;

end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
