CREATE OR REPLACE FUNCTION obter_se_categ_lib_multiestab (cd_convenio_p bigint, cd_categoria_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_liberado_w         		varchar(01)  := 'S';
qt_regra_w            		bigint;
cd_estabelecimento_usu_w  	usuario_estabelecimento.cd_estabelecimento%type;

c01 CURSOR FOR
  SELECT	cd_estabelecimento
  from  	usuario_estabelecimento
  where 	nm_usuario_param = nm_usuario_p;


BEGIN

select  count(*)
into STRICT  qt_regra_w
from  categoria_convenio_estab
where  cd_convenio = cd_convenio_p
and  cd_categoria = cd_categoria_p;

if (qt_regra_w  <> 0) then

  open c01;
	loop
	fetch c01 into
		cd_estabelecimento_usu_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
  begin

  select  coalesce(max('S'), 'N')
  into STRICT  ie_liberado_w
  from  categoria_convenio_estab
  where  cd_convenio = cd_convenio_p
  and  cd_categoria = cd_categoria_p
  and  cd_estabelecimento = cd_estabelecimento_usu_w;

  if (ie_liberado_w = 'S') then
	return ie_liberado_w;
  end if;

  end;
  end loop;
  close c01;

end if;

return  ie_liberado_w;

end;
$body$
