CREATE OR REPLACE FUNCTION obter_se_ccusto_setor ( cd_setor_atencimento_p bigint, cd_centro_custo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_w			bigint;
ie_existe_w			varchar(1) := 'N';

/*ie_opcao_p
1 - Requisição
2 - Solicitação*/
BEGIN

select	count(*)
into STRICT	qt_existe_w
from	setor_atendimento
where	cd_setor_atendimento	= cd_setor_atencimento_p
and	cd_centro_custo		= cd_centro_custo_p;

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
else
	if (ie_opcao_p = 1) then
		select	count(*)
		into STRICT	qt_existe_w
		from	sup_regra_centro_setor
		where	cd_setor_atendimento	= cd_setor_atencimento_p
		and	cd_centro_custo		= cd_centro_custo_p
		and	coalesce(ie_requisicao,'N')	= 'S'
		and	ie_situacao		= 'A';

		if (qt_existe_w > 0) then
			ie_existe_w := 'S';
		end if;
	end if;

	if (ie_opcao_p = 2) then
		select	count(*)
		into STRICT	qt_existe_w
		from	sup_regra_centro_setor
		where	cd_setor_atendimento	= cd_setor_atencimento_p
		and	cd_centro_custo		= cd_centro_custo_p
		and	coalesce(ie_solicitacao,'S')	= 'S'
		and	ie_situacao		= 'A';

		if (qt_existe_w > 0) then
			ie_existe_w := 'S';
		end if;
	end if;
end if;


return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
