SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_cc_lib ( nr_titulo_p bigint, cd_centro_custo_lib_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
cd_classificacao_w	centro_custo.cd_classificacao%type;
qt_centro_custo_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_centro_custo_w
from	titulo_pagar_classif a
where	(a.cd_centro_custo IS NOT NULL AND a.cd_centro_custo::text <> '')
and	a.nr_titulo		= nr_titulo_p;

/* se o título não tiver nenhum centro de custo, não precisa verificar o resto */

if (coalesce(qt_centro_custo_w,0)	= 0) then

	ds_retorno_w	:= 'N';

else

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	titulo_pagar_classif a
	where	a.cd_centro_custo	= cd_centro_custo_lib_p
	and	a.nr_titulo		= nr_titulo_p;

	if (ds_retorno_w	= 'N') then

		select	max(a.cd_classificacao)
		into STRICT	cd_classificacao_w
		from	centro_custo a
		where	a.cd_centro_custo	= cd_centro_custo_lib_p;

		if (cd_classificacao_w IS NOT NULL AND cd_classificacao_w::text <> '') then

			select	coalesce(max('S'),'N')
			into STRICT	ds_retorno_w
			from	centro_custo b,
				titulo_pagar_classif a
			where	b.cd_classificacao	like cd_classificacao_w || '%'
			and	a.cd_centro_custo	= b.cd_centro_custo
			and	a.nr_titulo		= nr_titulo_p;

		end if;

		if (ds_retorno_w	= 'N') then

			select	coalesce(max('S'),'N')
			into STRICT	ds_retorno_w
			from	titulo_pagar_classif a
			where	exists (SELECT	1
				from	centro_custo_lib_titulo x
				where	x.cd_centro_custo_lib	= a.cd_centro_custo
				and	x.cd_centro_custo	= cd_centro_custo_lib_p)
			and	(a.cd_centro_custo IS NOT NULL AND a.cd_centro_custo::text <> '')
			and	a.nr_titulo		= nr_titulo_p;

			if (ds_retorno_w	= 'N') then

				select	coalesce(max('S'),'N')
				into STRICT	ds_retorno_w
				from	centro_custo b,
					titulo_pagar_classif a
				where	exists (SELECT	1
					from	centro_custo y,
						centro_custo_lib_titulo x
					where	b.cd_classificacao	like y.cd_classificacao || '%'
					and	x.cd_centro_custo_lib	= y.cd_centro_custo
					and	x.cd_centro_custo	= cd_centro_custo_lib_p)
				and	a.cd_centro_custo	= b.cd_centro_custo
				and	a.nr_titulo		= nr_titulo_p;

			end if;

		end if;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_cc_lib ( nr_titulo_p bigint, cd_centro_custo_lib_p bigint) FROM PUBLIC;

