CREATE OR REPLACE FUNCTION obter_se_cep_cadastrado (cd_cep_p text) RETURNS varchar AS $body$
DECLARE


cd_cep_w	varchar(20);


BEGIN

select	max(a.cd_cep)
into STRICT	cd_cep_w
from	cep_log a
where	cd_cep	= cd_cep_p;

if (coalesce(cd_cep_w::text, '') = '') then
	select	max(a.nr_logradouro)
	into STRICT	cd_cep_w
	from	cep_logradouro a
	where	cd_logradouro	= cd_cep_p;
end if;

if (coalesce(cd_cep_w::text, '') = '') then
	select	max(a.cd_cep)
	into STRICT	cd_cep_w
	from	cep_loc a
	where	cd_cep	= cd_cep_p;
end if;

if (coalesce(cd_cep_w::text, '') = '') then
	return 'N';
else
	return 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
