CREATE OR REPLACE FUNCTION obter_se_checa_separado_gedipa ( nr_seq_processo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_checar_gedipa_w		varchar(1);


BEGIN

if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then

		select 	max(coalesce(c.ie_checar_gedipa,'N'))
		into STRICT	ie_checar_gedipa_w
		from   	prescr_material a,
				prescr_procedimento b,
				proc_int_mat_prescr c,
				prescr_mat_hor d
		where  	a.nr_prescricao 		= b.nr_prescricao
		and	  	a.nr_sequencia_proc 	= b.nr_sequencia
		and	   	b.nr_seq_proc_interno 	= c.nr_seq_proc_interno
		and	   	a.cd_material 			= c.cd_material
		and		a.nr_sequencia			= d.nr_seq_material
		and	   	c.cd_material 			= d.cd_material
		and	   	a.nr_prescricao 		= d.nr_prescricao
		and		d.nr_seq_etiqueta		= nr_seq_processo_p;

end if;

return ie_checar_gedipa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
