CREATE OR REPLACE FUNCTION obter_se_checklist_lib_perfil ( nr_seq_checklist_p bigint, cd_perfiel_p bigint, ie_tipo_prontuario_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255) := 'N';
ie_consiste_w  varchar(15) := 'N';


BEGIN

if (nr_seq_checklist_p IS NOT NULL AND nr_seq_checklist_p::text <> '' AND cd_perfiel_p IS NOT NULL AND cd_perfiel_p::text <> '') then
   select   coalesce(max('S'),'N')
   into STRICT     ie_consiste_w
   from     checklist_processo_lib
   where    nr_seq_checklist = nr_seq_checklist_p;

   if (ie_consiste_w = 'S') then
      select	max('S')
      into STRICT	   ds_retorno_w
      from	   checklist_processo_lib a
      where	   cd_perfil = cd_perfiel_p
      and	   a.nr_seq_checklist = nr_seq_checklist_p
      and (a.ie_tipo_prontuario = ie_tipo_prontuario_p or coalesce(ie_tipo_prontuario_p::text, '') = '');
   else
      ds_retorno_w := 'S';
   end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
