CREATE OR REPLACE FUNCTION obter_se_check_mat ( nr_seq_check_list_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'N';
qt_material_check_list_w	integer;


BEGIN

select	count(*)
into STRICT	qt_material_check_list_w
from	sl_item_check_list b,
	sl_check_list_unid_item a
where	a.nr_seq_checklist		= nr_seq_check_list_p
and	a.nr_seq_item_check_list	= b.nr_sequencia
and	a.ie_resultado			= 'S'
and	(b.cd_material IS NOT NULL AND b.cd_material::text <> '');

if (qt_material_check_list_w	> 0) then
	ds_Retorno_w	:= 'S';
end if;

return	ds_Retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
