CREATE OR REPLACE FUNCTION obter_se_cheque_cobranca ( nr_seq_cheque_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cheque_w	varchar(255);
count_w		double precision;


BEGIN

select	count(*)
into STRICT	count_w
from	cobranca
where	nr_seq_cheque	= nr_seq_cheque_p;

if (count_w > 0)	then
	ie_cheque_w 	:= 'S';
else
	ie_cheque_w 	:= 'N';
end if;

return	ie_cheque_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
