CREATE OR REPLACE FUNCTION obter_se_cheque_cr_avista (nr_seq_cheque_p bigint) RETURNS varchar AS $body$
DECLARE


dt_contabil_w			timestamp;
dt_vencimento_atual_w		timestamp;
dt_vencimento_w			timestamp;
ie_avista_w			varchar(50);


BEGIN

select	max(trunc(a.dt_contabil, 'dd')),
	max(trunc(a.dt_vencimento_atual, 'dd')),
	max(trunc(a.dt_vencimento, 'dd'))
into STRICT	dt_contabil_w,
	dt_vencimento_atual_w,
	dt_vencimento_w
from	cheque_cr a
where	a.nr_seq_cheque		= nr_seq_cheque_p;

ie_avista_w			:= 'S';
if (dt_vencimento_w IS NOT NULL AND dt_vencimento_w::text <> '') and (dt_contabil_w < coalesce(dt_vencimento_atual_w, dt_vencimento_w)) then
	ie_avista_w		:= 'N';
end if;

return	ie_avista_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
