CREATE OR REPLACE FUNCTION obter_se_cheque_negociado ( nr_seq_cheque_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cheque_w	varchar(255);
count_w		bigint;
vl_negociado_w	double precision;


BEGIN

select	count(*),
	coalesce(sum(vl_negociado),0)
into STRICT	count_w,
	vl_negociado_w
from	cheque_cr_negociado
where	nr_seq_cheque	= nr_seq_cheque_p;

if (coalesce(count_w,0)	= 0) then

	select	count(*),
		coalesce(sum(a.vl_transacao),0)
	into STRICT	count_w,
		vl_negociado_w
	from	transacao_financeira b,
		movto_trans_financ a
	where	b.ie_acao		= 30
	and	a.nr_seq_trans_financ	= b.nr_sequencia
	and	a.nr_seq_cheque		= nr_seq_cheque_p;

end if;

if (count_w > 0) and (vl_negociado_w > 0) then
	ie_cheque_w	:= 'S';
else
	ie_cheque_w	:= 'N';
end if;

return	ie_cheque_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
