SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_cheque_titulo (nr_seq_cheque_p bigint, nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(100);
cont_w			bigint;
nr_seq_caixa_w		bigint;
nr_seq_lote_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_seq_caixa,
	a.nr_seq_lote
from	movto_trans_financ a
where	a.nr_seq_cheque		= nr_seq_cheque_p
and	(a.nr_seq_caixa IS NOT NULL AND a.nr_seq_caixa::text <> '')
and	(a.nr_seq_lote IS NOT NULL AND a.nr_seq_lote::text <> '')
order 	by a.dt_transacao;



BEGIN

select	count(*)
into STRICT	cont_w
from	cheque_cr
where	nr_seq_cheque		= nr_seq_cheque_p
and	nr_titulo		= nr_titulo_p;

ie_retorno_w		:= 'N';
if (cont_w > 0) then
	ie_retorno_w	:= 'S';
else
	open c01;
	loop
	fetch c01 into
		nr_seq_caixa_w,
		nr_seq_lote_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		null;
	end loop;
	close c01;

	select	count(*)
	into STRICT	cont_w
	from	movto_trans_financ
	where	nr_seq_titulo_receber	= nr_titulo_p
	and	nr_seq_caixa		= nr_seq_caixa_w
	and	nr_seq_lote		= nr_seq_lote_w;

	if (cont_w > 0) then
		ie_retorno_w		:= 'S';
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
