CREATE OR REPLACE FUNCTION obter_se_ciclo_gera_prescr ( nr_seq_atendimento_p bigint, nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_autorizacao_w	bigint;
ds_retorno_w			varchar(1);
nr_ciclo_w				smallint;
ds_dia_ciclo_w			varchar(5);

BEGIN

select	max(nr_ciclo),
		max(ds_dia_ciclo)
into STRICT	nr_ciclo_w,
		ds_dia_ciclo_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_atendimento_p;

select	count(*)
into STRICT	qt_reg_autorizacao_w
from	autorizacao_convenio a,
		estagio_autorizacao b
where	a.nr_ciclo 		= nr_ciclo_w
and		((a.ds_dia_ciclo 		= ds_dia_ciclo_w) or (coalesce(a.ds_dia_ciclo, 'X') 	= 'X'))
and		a.nr_seq_paciente_setor 	= nr_seq_paciente_p
and		a.nr_seq_estagio		= b.nr_sequencia;

ds_retorno_w := 'S';
if (qt_reg_autorizacao_w > 0)  and (obter_se_mat_ciclo_autorizado(nr_seq_atendimento_p) = 'N') and (obter_estagio_autor_ciclo(nr_seq_atendimento_p, nr_seq_paciente_p,'I') <> '10') then
		ds_retorno_w := 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
