CREATE OR REPLACE FUNCTION obter_se_ciclo_suspenso ( nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE

qt_reg_w	bigint;
qt_reg_ww	bigint;

BEGIN


select	count(*)
into STRICT	qt_reg_w
from	paciente_atendimento
where	nr_seq_paciente	= nr_seq_paciente_p
and	(dt_cancelamento IS NOT NULL AND dt_cancelamento::text <> '');


select	count(*)
into STRICT	qt_reg_ww
from	paciente_atendimento
where	nr_seq_paciente	= nr_seq_paciente_p
and	coalesce(dt_cancelamento::text, '') = ''
and	substr(Obter_status_Paciente_qt(NR_SEQ_ATENDIMENTO,dt_inicio_adm,dt_fim_adm,nr_seq_local,ie_exige_liberacao,dt_chegada,'C'),1,60)	<> '83';

if (qt_reg_w	= 0) or (qt_reg_ww  >	0)  then
	return	'N';
else
	return 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
