CREATE OR REPLACE FUNCTION obter_se_cid_ativo ( cd_doenca_cid_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

select	coalesce(max(ie_situacao),'A')
into STRICT	ds_retorno_w
from	cid_doenca
where	cd_doenca_cid	= cd_doenca_cid_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
