CREATE OR REPLACE FUNCTION obter_se_cid_mat_especial ( cd_material_p bigint, cd_cid_p text, ie_tipo_cid_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
cd_procedimento_w	bigint;
qt_registro_w		bigint;


BEGIN

select	Obter_Proced_Mat_Esp_SUS(cd_material_p)
into STRICT	cd_procedimento_w
;

if (cd_procedimento_w > 0) then

	if (ie_tipo_cid_p = 'P') then

		select	count(*)
		into STRICT	qt_registro_w
		from	sus_procedimento_cid
		where	cd_procedimento		= cd_procedimento_w
		and	ie_origem_proced	= 7;

		if (qt_registro_w > 0) then

			select	count(*)
			into STRICT	qt_registro_w
			from	sus_procedimento_cid
			where	cd_procedimento		= cd_procedimento_w
			and	ie_origem_proced	= 7
			and	cd_doenca_cid		= cd_cid_p;

			if (qt_registro_w > 0) then
				ie_retorno_w		:= 'S';
			end if;

		else
			ie_retorno_w			:= 'S';
		end if;

	elsif (ie_tipo_cid_p = 'S') then

		select	count(*)
		into STRICT	qt_registro_w
		from	sus_procedimento_cid_sec
		where	cd_procedimento		= cd_procedimento_w
		and	ie_origem_proced	= 7;

		if (qt_registro_w > 0) then

			select	count(*)
			into STRICT	qt_registro_w
			from	sus_procedimento_cid_sec
			where	cd_procedimento		= cd_procedimento_w
			and	ie_origem_proced	= 7
			and	cd_doenca_cid_sec	= cd_cid_p;

			if (qt_registro_w > 0) then
				ie_retorno_w		:= 'S';
			end if;

		else
			ie_retorno_w			:= 'S';
		end if;
	end if;


end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
