CREATE OR REPLACE FUNCTION obter_se_cid_obrigatorio (cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE



ie_controle_medico_w	smallint;
ie_exige_cid_w			varchar(1)	:= 'N';
cd_perfil_w				integer;

C01 CURSOR FOR
SELECT	ie_exige_cid
from		regra_antimicrobiano
where	cd_regra			= ie_controle_medico_w
and		coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
and		cd_estabelecimento		= cd_estabelecimento_p
order by coalesce(cd_perfil,0),
		nr_sequencia desc;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	cd_perfil_w	:= obter_perfil_ativo;

	select	max(obter_controle_medic(cd_material,cd_estabelecimento_p,ie_controle_medico,null,null,null))
	into STRICT		ie_controle_medico_w
	from		material
	where	cd_material		=	cd_material_p;

	open C01;
	loop
	fetch C01 into
		ie_exige_cid_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_exige_cid_w	:= ie_exige_cid_w;
		end;
	end loop;
	close C01;

	return	ie_exige_cid_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
