SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_cig_pendente ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cig_w		char(1) := 'N';


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_cig_w
	from	atend_glicemia b,
			atendimento_cig a where		a.nr_atendimento = nr_atendimento_p
	and		b.nr_sequencia	= a.nr_seq_glicemia
	and		a.ie_status_cig = 'N'
	and		a.dt_proximo_controle < clock_timestamp()
	and		coalesce(b.ie_status_glic,'N') <> 'S'
	and		a.nr_sequencia = (	SELECT	max(x.nr_sequencia)
								from	atendimento_cig x
								where	x.nr_atendimento = nr_atendimento_p
								and		b.nr_sequencia	= x.nr_seq_glicemia) LIMIT 1;
end if;

return ie_cig_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
