CREATE OR REPLACE FUNCTION obter_se_cih_med_regra (cd_medicamento_p bigint, nr_seq_regra_controle_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
qt_regra_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	cih_regra_cont_medic_item
where 	nr_seq_regra_controle	= nr_seq_regra_controle_p
and	cd_medicamento		= cd_medicamento_p;

if (qt_regra_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
