CREATE OR REPLACE FUNCTION obter_se_cirurgia_agendada ( cd_pessoa_fisica_p text, nr_seq_proc_interno_p bigint ) RETURNS varchar AS $body$
DECLARE


qt_registro_w	smallint;


BEGIN

begin
select	count(*)
into STRICT	qt_registro_w
from	agenda_paciente
where	cd_pessoa_fisica		=	cd_pessoa_fisica_p
and	nr_seq_proc_interno	=	nr_seq_proc_interno_p
and	dt_agenda		>	clock_timestamp() - interval '1 days'
and	ie_status_agenda		<>	'C';
exception
	when others then
	qt_registro_w := 0;
end;

if (qt_registro_w > 0) then
	return	'S';
else
	return	'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
