CREATE OR REPLACE FUNCTION obter_se_cirurgia_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
qt_cirurgia_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_cirurgia_w
from	cirurgia
where	nr_atendimento		= nr_atendimento_p
and	(dt_inicio_real IS NOT NULL AND dt_inicio_real::text <> '')
and	ie_status_cirurgia	= '2';

if (qt_cirurgia_w > 0) then
	ds_retorno_w	:= 'S';
end if;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
