CREATE OR REPLACE FUNCTION obter_se_cirurgia_iniciada (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);

BEGIN

if (nr_sequencia_p > 0) then
	SELECT 	coalesce(max('S'),'N')
	INTO STRICT	ds_retorno_w
	FROM	cirurgia
	WHERE (dt_inicio_real IS NOT NULL AND dt_inicio_real::text <> '')
	  AND coalesce(dt_termino::text, '') = ''
	  AND coalesce(dt_cancelamento::text, '') = ''
	  AND nr_cirurgia = nr_sequencia_p;
end if;

RETURN	ds_retorno_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
