CREATE OR REPLACE FUNCTION obter_se_cirur_profilaxia ( nr_atendimento_p bigint, dt_prescricao_p timestamp, ie_somente_atb_prof_p text default 'S') RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
qt_reg_w			bigint;
qt_mat_tev_w		bigint;
qt_recomend_tev_w	bigint;


BEGIN

if (ie_somente_atb_prof_p = 'E') then

	select 	count(*)
	into STRICT	qt_reg_w
	from	cirurgia
	where	nr_atendimento	= nr_atendimento_p
	and		IE_STATUS_CIRURGIA = '2'
	and		dt_prescricao_p  between(dt_inicio_real - 2) and ( dt_inicio_real + 2);

elsif (ie_somente_atb_prof_p = 'L') then

	select 	count(*)
	into STRICT	qt_reg_w
	from	cirurgia
	where	nr_atendimento	= nr_atendimento_p
	and		IE_STATUS_CIRURGIA = '2'
	and		((dt_prescricao_p  < (dt_inicio_real - 2)) or
			(dt_prescricao_p  > (dt_inicio_real + 2)));

elsif (ie_somente_atb_prof_p = 'S') then

	select 	count(*)
	into STRICT	qt_reg_w
	from	cirurgia
	where	nr_atendimento	= nr_atendimento_p
	and		IE_STATUS_CIRURGIA = '2';

end if;

if (qt_reg_w > 0) then

	ds_retorno_w	:= 'S';

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
