CREATE OR REPLACE FUNCTION obter_se_cir_video (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01);


BEGIN

ds_retorno_w	:= 'N';

select	coalesce(max(ie_video),'N')
into STRICT	ds_retorno_w
from	cirurgia
where	nr_cirurgia = nr_cirurgia_p;

if (ds_retorno_w = 'N') then
	SELECT	coalesce(MAX('S'),'N')
	INTO STRICT	ds_retorno_w
	FROM	procedimento_paciente
	WHERE	nr_cirurgia	= nr_cirurgia_p
	AND	ie_video	= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
