CREATE OR REPLACE FUNCTION obter_se_ci_contem_texto ( ds_lista_ci_busca_p text, ds_texto_busca_p text) RETURNS varchar AS $body$
DECLARE


ds_lista_ci_resultado_w	varchar(2000) := '0';
ds_lista_ci_busca_w	varchar(2000);
nr_controle_loop_w	smallint := 0;
nr_pos_virgula_w	smallint;
nr_seq_ci_w		bigint;
ie_contem_w		varchar(1);


BEGIN
if (ds_lista_ci_busca_p IS NOT NULL AND ds_lista_ci_busca_p::text <> '') and (ds_texto_busca_p IS NOT NULL AND ds_texto_busca_p::text <> '') then
	begin
	ds_lista_ci_busca_w := ds_lista_ci_busca_p;

	while(ds_lista_ci_busca_w IS NOT NULL AND ds_lista_ci_busca_w::text <> '') and (nr_controle_loop_w < 100) loop
		begin
		ie_contem_w		:= 'N';
		nr_seq_ci_w		:= 0;
		nr_pos_virgula_w	:= position(',' in ds_lista_ci_busca_w);

		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_ci_w		:= (substr(ds_lista_ci_busca_w,1,nr_pos_virgula_w-1))::numeric;
			ds_lista_ci_busca_w	:= substr(ds_lista_ci_busca_w,nr_pos_virgula_w+1,length(ds_lista_ci_busca_w));
			end;
		elsif (ds_lista_ci_busca_w IS NOT NULL AND ds_lista_ci_busca_w::text <> '') then
			begin
			nr_seq_ci_w		:= (ds_lista_ci_busca_w)::numeric;
			ds_lista_ci_busca_w	:= null;
			end;
		end if;

		if (nr_seq_ci_w > 0) then
			begin
			ie_contem_w := obter_se_long_contem_texto('COMUNIC_INTERNA', 'DS_COMUNICADO', 'where nr_sequencia = :nr_seq_ci','nr_seq_ci='||nr_seq_ci_w, ds_texto_busca_p);

			if (ie_contem_w = 'S') then
				begin
				ds_lista_ci_resultado_w := ds_lista_ci_resultado_w ||','|| to_char(nr_seq_ci_w);
				end;
			end if;
			end;
		end if;

		nr_controle_loop_w := nr_controle_loop_w + 1;
		end;
	end loop;
	end;
end if;
return ds_lista_ci_resultado_w;
end;
