CREATE OR REPLACE FUNCTION obter_se_clasif_setor_lib (cd_classif_setor_p bigint, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'S';
qt_regra_w	bigint;
qt_regra_classif_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	agenda_classif_setor
where	coalesce(cd_agenda,cd_agenda_p)	= cd_agenda_p
and	coalesce(ie_situacao,'A') = 'A';

if (qt_regra_w > 0) then
	select	count(*)
	into STRICT	qt_regra_classif_w
	from	agenda_classif_setor
	where	coalesce(cd_agenda,cd_agenda_p)	= cd_agenda_p
	and	cd_classif_setor		= cd_classif_setor_p;

	if (qt_regra_classif_w > 0) then
		ie_retorno_w := 'S';
	else
		ie_retorno_w := 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
