SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_classif_alt_valor (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


vl_classif_w		double precision;
vl_alteracao_w		double precision;
nr_sequencia_w		bigint;
ds_retorno_w		varchar(2);


BEGIN

select	coalesce(sum(b.vl_titulo),0)
into STRICT	vl_classif_w
from	titulo_pagar_classif b
where	b.nr_titulo	= nr_titulo_p;

select	coalesce(max(x.nr_sequencia),0)
into STRICT	nr_sequencia_w
from	titulo_pagar_alt_valor x
where	x.nr_titulo	= nr_titulo_p;

select	coalesce(max(y.vl_alteracao),0)
into STRICT	vl_alteracao_w
from	titulo_pagar_alt_valor y
where	y.nr_titulo	= nr_titulo_p
and	y.nr_sequencia	= nr_sequencia_w;

ds_retorno_w	:= 'N';
if (vl_alteracao_w = vl_classif_w) then
	ds_retorno_w	:= 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
