SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_classif_braden ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	IE_OPCAO_P
DR = Primeiro registro
DA = Ultimo registro antes da data dt_evento_w
*/
dt_registro_w			varchar(50);
ds_resultado_w			varchar(255);
ds_retorno_w			varchar(255) := 'N';
dt_evento_w				timestamp;
nr_atendimento_w		bigint;
qt_ponto_w				varchar(4);
ds_resultado_escala_w	varchar(255);


BEGIN
select  max(a.nr_atendimento),
		max(substr(obter_resultado_braden(a.qt_ponto),1,255))
into STRICT	nr_atendimento_w,
		ds_resultado_escala_w
from	atend_escala_braden a
where	a.nr_sequencia  = nr_sequencia_p;

if (ie_opcao_p = 'DR') and (nr_atendimento_w > 0) then

	Select	max(substr(obter_resultado_braden(qt_ponto),1,255))
	into STRICT	ds_resultado_w
	from	atend_escala_braden
	where	nr_sequencia = (  SELECT	min(nr_sequencia)
								from	atend_escala_braden x
								where	x.nr_atendimento = nr_atendimento_w
								and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
								and		coalesce(x.dt_inativacao::text, '') = ''	);

	if (ds_resultado_w = ds_resultado_escala_w) then

		ds_retorno_w := 'S';

	end if;

elsif (ie_opcao_p = 'DA') and (nr_atendimento_w > 0) and (dt_evento_w IS NOT NULL AND dt_evento_w::text <> '') then

	Select	max(substr(obter_resultado_braden(qt_ponto),1,255))
	into STRICT	ds_resultado_w
	from	atend_escala_braden
	where	nr_sequencia = (  SELECT	max(nr_sequencia)
								from	atend_escala_braden x
								where	x.nr_atendimento = nr_atendimento_w
								and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
								and		coalesce(x.dt_inativacao::text, '') = ''	);

	if (ds_resultado_w = ds_resultado_escala_w) then

		ds_retorno_w := 'S';

	end if;


end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
