CREATE OR REPLACE FUNCTION obter_se_classif_dieta_perfil ( nr_seq_classif_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'S';
qt_rec_w	bigint;

BEGIN


Select	count(*)
into STRICT	qt_rec_w
from 	DIETA_CLASSIF_PERFIL
where	NR_SEQ_CLASSIF	= nr_seq_classif_p;

if (qt_rec_w > 0) then
	Select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from 	DIETA_CLASSIF_PERFIL
	where	NR_SEQ_CLASSIF	= nr_seq_classif_p
	and 	cd_perfil	= cd_perfil_p;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
