CREATE OR REPLACE FUNCTION obter_se_classif_dup (cd_classif_conta_p text, cd_conta_contabil_p text, cd_empresa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
ie_classif_conta_w		varchar(01);
qt_reg_w			bigint;

BEGIN

select	ie_classif_conta
into STRICT	ie_classif_conta_w
from	empresa
where	cd_empresa	= cd_empresa_p;


if (ie_classif_conta_w = 'N') then
	select	count(*)
	into STRICT	qt_reg_w
	from	conta_contabil
	where	cd_classificacao		= cd_classif_conta_p
	and	cd_conta_contabil		<> cd_conta_contabil_p
	and	substr(obter_se_conta_vigente2(cd_conta_contabil, dt_inicio_vigencia, dt_fim_vigencia, clock_timestamp()),1,1) = 'S'
	and	cd_empresa 		= cd_empresa_p;
	if (qt_reg_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
