CREATE OR REPLACE FUNCTION obter_se_classif_envia_sms (cd_estabelecimento_p bigint, ie_classificacao_p text) RETURNS varchar AS $body$
DECLARE


qt_regra_envio_w  integer;
ie_tipo_regra_w	regra_classif_envio_sms.ie_tipo_regra%type;
ds_retorno_w    varchar(1) := 'S';


BEGIN

select  count(*)
into STRICT  qt_regra_envio_w
from  regra_classif_envio_sms
where ie_situacao = 'A'
and cd_estabelecimento = cd_estabelecimento_p;

if (qt_regra_envio_w > 0) then

	select max(ie_tipo_regra)
	into STRICT ie_tipo_regra_w
	from (
		SELECT  ie_tipo_regra
		from  regra_classif_envio_sms
		where ie_situacao = 'A'
		and cd_estabelecimento = cd_estabelecimento_p
		and (ie_classif_agenda = ie_classificacao_p
		or coalesce(ie_classif_agenda::text, '') = '')
		order by ie_classif_agenda) alias4 LIMIT 1;

	if (ie_tipo_regra_w = 'B') then
		ds_retorno_w := 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
