CREATE OR REPLACE FUNCTION obter_se_classif_exibe_quimio ( nr_seq_classif_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10) := 'N';


BEGIN

select	coalesce(MAX('S'),'N')
into STRICT 	ds_retorno_w
from 	CLASSIF_PESSOA
where	nr_sequencia = nr_seq_classif_p
and 	coalesce(IE_EXIBE_QUIMIOTERAPIA,'N') = 'S';

return	 coalesce(ds_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
