CREATE OR REPLACE FUNCTION obter_se_classif_pf_lib_usu ( nr_seq_classif_pf_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01) := 'S';
qt_existe_w		bigint;
cd_perfil_w		integer := obter_perfil_ativo;
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;

c01 CURSOR FOR
SELECT	'S'
from	classif_pessoa_regra_lib
where	coalesce(cd_estab_lib,cd_estabelecimento_w)	= cd_estabelecimento_w
and	coalesce(nm_usuario_lib,nm_usuario_p)		= nm_usuario_p
and	coalesce(cd_perfil, cd_perfil_w)			= cd_perfil_w
and	nr_seq_classif_pessoa			= nr_seq_classif_pf_p
order by	nm_usuario_lib desc,
	coalesce(cd_perfil,0),
	coalesce(cd_estab_lib,0),
	nr_sequencia;


BEGIN
select	count(*)
into STRICT	qt_existe_w
from	classif_pessoa_regra_lib
where	nr_seq_classif_pessoa = nr_seq_classif_pf_p;

if (qt_existe_w > 0) then
	ds_retorno_w := 'N';
	open C01;
	loop
	fetch C01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;
end if;

return	ds_retorno_w;

end;
$body$
