SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_classif_problema_lib ( nr_seq_classif_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	L - Apenas liberação
	A - Liberação e alteração		*/
cd_perfil_w		integer := obter_perfil_ativo;
qt_existe_w		bigint;
ie_retorno_w		varchar(1) := 'S';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	problema_classif_perfil
where	nr_seq_classif = nr_seq_classif_p;

if (qt_existe_w > 0) then
	begin
	if (ie_opcao_p = 'L') then
		begin
		select	'S'
		into STRICT	ie_retorno_w
		from	problema_classif_perfil
		where	cd_perfil = cd_perfil_w
		and	nr_seq_classif = nr_seq_classif_p;
		exception
		when others then
			ie_retorno_w := 'N';
		end;
	elsif (ie_opcao_p = 'A') then
		begin
		select	ie_atualizar
		into STRICT	ie_retorno_w
		from	problema_classif_perfil
		where	cd_perfil = cd_perfil_w
		and	nr_seq_classif = nr_seq_classif_p;
		exception
		when others then
			ie_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
