CREATE OR REPLACE FUNCTION obter_se_classif_setor_obito (cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_classif_obito_w	varchar(2);
ie_setor_obito_w	varchar(1) := 'N';
vl_classif_param_w	varchar(15);


BEGIN

vl_classif_param_w := Obter_Param_Usuario(3111, 260, Obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, vl_classif_param_w);

if (coalesce(cd_setor_atendimento_p,0) > 0) then
	if (vl_classif_param_w IS NOT NULL AND vl_classif_param_w::text <> '') then
		select 	substr(obter_classif_setor(cd_setor_atendimento_p),1,1)
		into STRICT	ie_classif_obito_w
		;

		if (substr(obter_se_contido((ie_classif_obito_w)::numeric , vl_classif_param_w),1,1) = 'S')	then
			ie_setor_obito_w	:= 'S';
		else
			ie_setor_obito_w	:= 'N';
		end if;
	else
		ie_setor_obito_w	:= 'S';
	end if;
end if;

return	ie_setor_obito_w;

end;
$body$
