CREATE OR REPLACE FUNCTION obter_se_cliente_canal_venda ( nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


ie_canal_venda_w	varchar(1) := 'N';


BEGIN
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_canal_venda_w
	from	com_canal_cliente
	where	nr_seq_cliente = nr_seq_cliente_p
	and	ie_tipo_atuacao = 'V'
	and	coalesce(dt_fim_atuacao::text, '') = '';
	end;
end if;
return ie_canal_venda_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
