CREATE OR REPLACE FUNCTION obter_se_cliente_usa_ios ( qt_dias_p bigint) RETURNS varchar AS $body$
DECLARE


/*
qt_dias_p = Quantidade de dias retroativos que se deseja verificar a existencia de acessos.  Padrao 30 dias.
*/
ds_retorno_w	varchar(1);


BEGIN

select  coalesce(max('S'),'N')
into STRICT	ds_retorno_w

where   exists (        SELECT  1
                        from    tasy_log_acesso
                        where   dt_acesso > clock_timestamp() - qt_dias_p
                        and     cd_aplicacao_tasy = 'iOS');

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_cliente_usa_ios ( qt_dias_p bigint) FROM PUBLIC;

