CREATE OR REPLACE FUNCTION obter_se_clinica_curativo ( ie_clinica_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2);
qt_reg_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	regra_clinica_curativo;

if (qt_reg_w = 0) or (coalesce(ie_clinica_p::text, '') = '') then
	ds_retorno_w	:= 'S';
else
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	regra_clinica_curativo
	where	ie_clinica = ie_clinica_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
