CREATE OR REPLACE FUNCTION obter_se_clinica_idade ( qt_idade_p bigint, ie_clinica_p bigint, ie_tipo_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_clinica_w	bigint;
ie_permite_w	varchar(1) := 'N';
qt_clinicas_w	bigint;

c01 CURSOR FOR
	SELECT	ie_clinica
	from	regra_clinica_idade
	where	qt_idade_p between qt_idade_min and qt_idade_max
	and	((ie_tipo_atendimento = ie_tipo_atendimento_p) or (coalesce(ie_tipo_atendimento::text, '') = ''))
	and	((cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento) or (coalesce(cd_estabelecimento::text, '') = ''));

BEGIN

select	count(*)
into STRICT	qt_clinicas_w
from	regra_clinica_idade
where	qt_idade_p between qt_idade_min and qt_idade_max
and	((ie_tipo_atendimento = ie_tipo_atendimento_p) or (coalesce(ie_tipo_atendimento::text, '') = ''))
and	((cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento) or (coalesce(cd_estabelecimento::text, '') = ''));

If (qt_clinicas_w = 0) then
	ie_permite_w := 'S';
End if;

open c01;
loop
fetch 	c01 into
	ie_clinica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	If (ie_permite_w <> 'S') then
		If (ie_clinica_w = ie_clinica_p) then
			ie_permite_w := 'S';
		Else
			ie_permite_w := 'N';
		End if;
	End if;
end loop;
close c01;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
