SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_cnpj_valido ( nr_cnpj_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_cnpj_number_w	varchar(20);
nr_digito_ver_1_w	integer;
nr_digito_ver_2_w	integer;
nr_coeficiente_w	integer;
nr_digito_calc_w	integer;
vl_soma_w		bigint;
primeiro_digito_cnpj_w	integer;
qt_numeros_iguais_w	integer;
BEGIN
ds_retorno_w := 'N';

-- se tiver valor informado
if (length(nr_cnpj_p) > 0) then

	-- retira qualquer coisa que não for número
	nr_cnpj_number_w := regexp_replace(nr_cnpj_p, '[^0-9]', '');

	-- só é valido se tiver 14 posições
	if (length(nr_cnpj_number_w) = 14) then

		-- tratamento para números repetidos
		if (nr_cnpj_number_w not in (	'11111111111111', '22222222222222', '33333333333333',
							'44444444444444', '55555555555555', '66666666666666',
							'77777777777777', '88888888888888', '99999999999999')) then

			nr_digito_ver_1_w := (substr(nr_cnpj_number_w, 13, 1))::numeric;
			nr_digito_ver_2_w := (substr(nr_cnpj_number_w, -1))::numeric;

			vl_soma_w := 0;
			nr_coeficiente_w := 2;
			FOR i_w IN REVERSE 12..1 loop
				vl_soma_w := vl_soma_w + ((substr(nr_cnpj_number_w, i_w, 1))::numeric  * nr_coeficiente_w);

				nr_coeficiente_w := nr_coeficiente_w + 1;
				if (nr_coeficiente_w > 9) then
					nr_coeficiente_w := 2;
				end if;
			end loop;

			nr_digito_calc_w := 11 - mod(vl_soma_w, 11);

			if (nr_digito_calc_w > 9) then
				nr_digito_calc_w := 0;
			end if;

			-- se o primeiro dígito verificador é válido
			if (nr_digito_calc_w = nr_digito_ver_1_w) then

				vl_soma_w := 0;
				nr_coeficiente_w := 2;
				FOR i_w IN REVERSE 13..1 loop
					vl_soma_w := vl_soma_w + ((substr(nr_cnpj_number_w, i_w, 1))::numeric  * nr_coeficiente_w);

					nr_coeficiente_w := nr_coeficiente_w + 1;
					if (nr_coeficiente_w > 9) then
						nr_coeficiente_w := 2;
					end if;
				end loop;

				nr_digito_calc_w := 11 - mod(vl_soma_w, 11);

				if (nr_digito_calc_w > 9) then
					nr_digito_calc_w := 0;
				end if;

				-- se o segundo dígito é válido
				if (nr_digito_calc_w = nr_digito_ver_2_w) then
					ds_retorno_w := 'S';
				end if;
			end if;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
