CREATE OR REPLACE FUNCTION obter_se_cobra_paciente ( cd_convenio_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN

select ie_cobra_paciente
into STRICT ie_retorno_w
from material
where cd_material = cd_material_p;

if (ie_retorno_w = 'N') then
	select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT ie_retorno_w
	from material_convenio
	where cd_convenio = cd_convenio_p
	  and cd_material_conta = cd_material_p;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
