CREATE OR REPLACE FUNCTION obter_se_cod_conv_duplic (cd_codigo_convenio_p text) RETURNS varchar AS $body$
DECLARE

 
/*retorna se um nÃºmero de carteirinha esta duplicado no sistema*/
 
 
ie_duplicado_w varchar(1) := 'N';
qt_codigo_convenio_w bigint := 0;


BEGIN 
select	count(distinct a.cd_pessoa_fisica) 
into STRICT	qt_codigo_convenio_w 
from	atendimento_paciente a, 
		atend_categoria_convenio b 
where	a.nr_atendimento = b.nr_atendimento 
and		b.cd_usuario_convenio = cd_codigo_convenio_p;
 
if (qt_codigo_convenio_w	> 1) then 
	ie_duplicado_w := 'S';
end if;
 
return	ie_duplicado_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
