CREATE OR REPLACE FUNCTION obter_se_cod_interno_valido ( cd_barras_p text, ie_tipo_doc_interno_p text) RETURNS varchar AS $body$
DECLARE

ie_cod_valido_w		varchar(1);
ie_tipo_doc_interno_w	varchar(15):='B';


BEGIN

if (ie_tipo_doc_interno_p IS NOT NULL AND ie_tipo_doc_interno_p::text <> '') then
	ie_tipo_doc_interno_w := ie_tipo_doc_interno_p;
end if;

if (ie_tipo_doc_interno_w = 'B') then
	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_cod_valido_w
	from 	usuario
	where 	cd_barras = cd_barras_p;
elsif (ie_tipo_doc_interno_w = 'C') then
	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_cod_valido_w
	from 	medico
	where 	nr_crm = cd_barras_p;
end if;

return ie_cod_valido_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
